    // console.log("level2 = " + level2);
    let lowMod2 = level2 * 100;
    let highMod2 = level2 * 800;

  // LEFT ELLIPSE MOUTHS MODULATION
  noStroke();
  fill(255 - highMod1/2, 0, 255 - highMod1, 220);
  // lips are a circles
  ellipse(100, height / 2, width/4 - 20 , width/4);
