let my = {
  // update to verify change on mobile
  version: '?v=030',
  width: 240,
  height: 320,
  // width: 480,
  // height: 640,
  vFlip: 0, 
  facingMode: 'user',
  face: 1,
  showVideo: 1,
  soundEnable: 0,
  fcount: 1,
  record: 0,
  replay: 0,
  uid: '-none-',
  // scale: 16,
  // Aspect ratio of video capture/
  // will be scaled by rezScale
  rezScale: 4,
  // rezScale: 1,
  // imageQuality: 0.01,
  imageQuality: 1,
  captionScale: 32,
  interval: 1,
  debugLog: 1,
  count_base: 100,
  count_max: 5,
  colors: ['red', 'green', 'gold'],
  colorIndex: 1,
  logLinesMax: 3,
  clipsName: 'clips',
  recordCountDown: {
    max: 3,
    next_secs: 0,
  },
};

function setup() {
  console.log('mo-storage-video setup');

  my_init();

  my.canvas = createCanvas(my.width, my.height);

  ui_init();

  let config = fb_.init('jht9629');
  // let config = fb_.init('jht1493');
  ui_log('config.projectId', config.projectId);

  fb_signIn();

  textSize(40);
}

function draw() {
  //
  draw_update();

  ui_update();

  sound_draw();
}

function draw_update() {
  if (!video_ready()) return;

  // faster to get entire video frame as an image
  let img = my.video.get();

  let layer = my.layer;

  if (my.showVideo) {
    layer.image(img, 0, 0);
  }

  draw_number(my.count + 1 + my.count_base, { layer });
  draw_millis(layer);
  draw_dateISOString(layer);

  if (my.replay) {
    image(my.replayLayer, 0, 0, width, height);
  } else {
    image(layer, 0, 0, width, height);
  }

  record_state_track();

  if (!my.record_preroll) {
    draw_count();
  }

  let now = millis() / 1000;
  if (now > my.next_secs) {
    my.next_secs = now + my.interval;
    update_interval();
  }
}

function update_interval() {
  // console.log('update_interval my.count', my.count);

  if (my.replay) {
    fstore_img_download();
    if (my.soundEnable) {
      sound_playback_start();
    }
  } else {
    sound_playback_stop();
  }

  if (my.record_preroll) {
    return;
  }

  if (my.record) {
    fstore_img_upload();
    if (my.count == 0 && my.soundEnable) {
      sound_record_start();
    }
  }

  if (my.fcount) {
    if (adjust_count(1) && my.record) {
      my.record = 0;
      my.recordChk.checked(0);
      my.replay = 1;
      my.replayChk.checked(1);
      if (my.soundEnable) {
        sound_record_stop();
      }
    }
  }
}

function adjust_count(delta) {
  my.count = my.count + delta;
  let wrap = 0;
  if (my.count >= my.count_max) {
    my.count = 0;
    wrap = 1;
  } else if (my.count < 0) {
    my.count = my.count_max - 1;
    wrap = 1;
  }
  return wrap;
}

function fb_signIn() {
  let { signInAnonymously, auth } = fb_;
  signInAnonymously(auth)
    .then(() => {
      ui_log('signInAnonymously OK');
      my.uid = auth.currentUser.uid;
    })
    .catch((error) => {
      const errorCode = error.code;
      const errorMessage = error.message;
      ui_log('fb_signIn errorCode', errorCode);
      ui_log('fb_signIn errorMessage', errorMessage);
    });
}

// https://en.wikipedia.org/wiki/15_minutes_of_fame
// --> 15 seconds

// setInterval will flood out draw when saving and replaying
// setInterval(update_interval, my.interval * 1000);
// frameRate(2);

function draw_number(num, opt) {
  let { layer, right, bottom, frac, noid, captionSize } = opt;

  if (!captionSize) captionSize = my.captionSize;
  layer.textSize(captionSize);

  let str = num.toString();
  if (frac) {
    str = convert_timeFrac(num, frac);
  }
  str = str.padStart(my.image_seq_pad, '0');

  let tw = layer.textWidth(str);
  let th = layer.textLeading();
  let ta = layer.textAscent();

  let x = 0;
  let y = 0;
  if (right) {
    x = layer.width - tw;
  } else {
    tw = layer.width / 2;
    if (!noid) {
      str += ' ' + my.uid.substring(0, 7);
    }
  }
  if (bottom) {
    y = layer.height - th;
  }
  let colr = my.colors[my.colorIndex];
  if (right) colr = 0;
  layer.fill(colr);
  layer.rect(x, y, tw, th);
  layer.fill(255);
  layer.text(str, x, y + ta);
}

function convert_timeFrac(num, frac) {
  let sec = int(num);
  let fsec = int((num - sec) * 100);
  let min = int(sec / 60);
  sec -= min * 60;
  let hours = int(min / 60);
  min -= hours * 60;
  // console.log('hours', hours, 'min', min, 'sec', sec, 'num', num);
  let str = '.' + fsec.toString().padStart(2, 0);
  if (sec != 0 || min != 0 || hours != 0) {
    str = sec.toString().padStart(2, '0') + str;
  }
  if (min != 0 || hours != 0) {
    str = min.toString().padStart(2, '0') + ':' + str;
  }
  if (hours != 0) {
    str = hours.toString().padStart(2, '0') + ':' + str;
  }
  return str;
}

function draw_dateISOString(layer) {
  let today = new Date();
  let str = today.toISOString();

  let capSize = my.captionSize;
  layer.textSize(capSize);

  let tw = layer.textWidth(str);
  let th = layer.textLeading();
  let ta = layer.textAscent();

  let x = layer.width - tw;
  let y = layer.height - th;

  // let colr = my.colors[my.colorIndex];
  layer.fill(0);
  layer.rect(x, y, tw, th);
  layer.fill(255);
  layer.text(str, x, y + ta);
}

function draw_millis(layer) {
  let fmt = { layer, right: 1, small: 1, frac: 2 };
  draw_number(millis() / 1000, fmt);
}

