function frame_count_str() {
  let num = my.count + my.count_base + 1;
  let str = num.toString();
  str = str.padStart(my.image_seq_pad, '0');
  // Select the file name from the last download
  if (my.replay) {
    if (my.lastDownloadPath) {
      let parts = my.lastDownloadPath.split('/');
      if (parts.length > 0) {
        str = parts[parts.length - 1];
      }
    } else {
      str = '';
    }
  }
  return str;
}

function create_myVideo() {
  let options = { video: { facingMode: my.facingMode } };
  my.video = createCapture(options);
  my.video.size(my.vwidth, my.vheight);
  my.video.hide();
}

function video_ready() {
  return my.video.loadedmetadata && my.video.width > 0 && my.video.height > 0;
}

//
