function fstore_img_upload() {
  // console.log('fstore_img_upload');
  let count = my.count;
  my.layer.elt.toBlob(
    (blob) => {
      fstore_img_upload_blob(blob, count);
    },
    my.imageType,
    my.imageQuality
  );
}

function fstore_img_upload_blob(blob, count) {
  // console.log('fstore_img_upload_blob', blob);
  let { storage, ref, uploadBytes } = fb_.fstore;
  // let path = `/-mo-1/${fb_.auth.currentUser.uid}/000`;
  my.imagePath = next_imagePath(count);
  // ui_log('fstore_img_upload_blob my.imagePath', my.imagePath);
  const storageRef = ref(storage, my.imagePath);

  // 'file' comes from the Blob or File API
  uploadBytes(storageRef, blob)
    .then((snapshot) => {
      // ui_log('snapshot.metadata.fullPath ' + snapshot.metadata.fullPath);
      // console.log('snapshot', snapshot);
      // console.log('Uploaded path', path);
      ui_log('upload ', my.imagePath);
    })
    .catch((error) => {
      // Handle any errors
      ui_error('fstore_img_upload_blob error', error);
    });
}

function next_imagePath(count) {
  // console.log('next_imagePath');
  let str = (count + my.count_base + 1).toString().padStart(my.image_seq_pad, '0');
  return `${my.dbStoreRootPath}/${my.clipsName}/${str}${my.imagExt}`;
}

// https://firebase.google.com/docs/storage/web/list-files#list_all_files
function fstore_listAll(bucket) {
  // console.log('fstore_listAll bucket', bucket);
  let { storage, ref, listAll } = fb_.fstore;
  bucket = bucket || my.dbStoreRootPath;
  console.log('fstore_listAll bucket', bucket);
  // Create a reference under which you want to list
  // const listRef = ref(storage, 'oVFxc052pOWF5qq560qMuBmEsbr2');
  // const listRef = ref(storage, '');
  const listRef = ref(storage, bucket);
  // console.log('listRef', listRef);
  // Find all the prefixes and items.
  listAll(listRef)
    .then((res) => {
      res.prefixes.forEach((folderRef) => {
        // All the prefixes under listRef.
        // You may call listAll() recursively on them.
        // console.log('folderRef', folderRef);
        // console.log('folderRef.path', folderRef.path); // Defined
        // console.log('bucket', folderRef.bucket);
        console.log('prefix fullPath', folderRef.fullPath);
      });
      res.items.forEach((itemRef) => {
        // All the items under listRef.
        // console.log('itemRef', itemRef);
        console.log('item fullPath', itemRef.fullPath);
      });
    })
    .catch((error) => {
      // Uh-oh, an error occurred!
      ui_error('fstore_listAll error', error);
    });
}

// https://firebase.google.com/docs/storage/web/list-files#paginate_list_results
function fstore_list(bucket) {
  console.log('fstore_list bucket', bucket);
  let { storage, ref, list } = fb_.fstore;
  bucket = bucket || my.dbStoreRootPath;
  // Create a reference under which you want to list
  // const listRef = ref(storage, 'oVFxc052pOWF5qq560qMuBmEsbr2');
  // const listRef = ref(storage, '');
  const listRef = ref(storage, bucket);
  console.log('listRef', listRef);
  // Find all the prefixes and items.
  list(listRef, { maxResults: 100 })
    .then((res) => {
      res.prefixes.forEach((folderRef) => {
        // All the prefixes under listRef.
        // You may call listAll() recursively on them.
        // console.log('folderRef', folderRef);
        // console.log('folderRef.path', folderRef.path); // Defined
        // console.log('bucket', folderRef.bucket);
        console.log('prefix fullPath', folderRef.fullPath);
      });
      res.items.forEach((itemRef) => {
        // All the items under listRef.
        // console.log('itemRef', itemRef);
        console.log('item fullPath', itemRef.fullPath);
      });
    })
    .catch((error) => {
      // Uh-oh, an error occurred!
      ui_error('fstore_list error', error);
    });
}

function sound_getBlob() {
  my.soundBlob = my.soundFile.getBlob();
  // Upload the sound blob
  fstore_sound_upload_blob(my.soundBlob, 0);
}

function fstore_sound_upload_blob(blob, count) {
  console.log('fstore_sound_upload_blob', blob);
  let { storage, ref, uploadBytes } = fb_.fstore;
  my.soundPath = next_soundPath(count);
  ui_log('fstore_sound_upload_blob my.soundPath', my.soundPath);
  const storageRef = ref(storage, my.soundPath);
  // 'file' comes from the Blob or File API
  uploadBytes(storageRef, blob)
    .then((snapshot) => {
      // ui_log('snapshot.metadata.fullPath ' + snapshot.metadata.fullPath);
      // console.log('snapshot', snapshot);
      // console.log('Uploaded path', path);
      ui_log('upload ', my.soundPath);
      my.fstore_sound_upload_completed = 1;
    })
    .catch((error) => {
      // Handle any errors
      ui_error('fstore_sound_upload_blob error', error);
    });
}

function next_soundPath(count) {
  // console.log('next_soundPath');
  let str = (count + my.count_base + 1).toString().padStart(my.image_seq_pad, '0');
  return `${my.dbStoreRootPath}/${my.clipsName}/${str}.wav`;
}

function fstore_sound_download() {
  // console.log('fstore_sound_download ');
  let path = next_soundPath(0);
  // ui_log('fstore_sound_download next_imagePath ' + path);
  let { storage, ref, getDownloadURL } = fb_.fstore;
  getDownloadURL(ref(storage, path))
    .then((url) => {
      const xhr = new XMLHttpRequest();
      xhr.responseType = 'blob';
      xhr.onload = (event) => {
        const blob = xhr.response;
        // ui_log('fstore_sound_download blob ' + blob);
        ui_log('sound download ' + path);
        sound_play_blob(blob);
      };
      xhr.open('GET', url);
      xhr.send();
    })
    .catch((error) => {
      // Handle any errors
      ui_error('fstore_sound_download error', error);
    });
}
function sound_play_blob(blob) {
  my.sound_download_blob = blob;
  const reader = new FileReader();
  reader.onload = function (e) {
    const srcUrl = e.target.result;
    // console.log('sound_play_blob srcUrl', srcUrl);
    my.sound_srcUrl = srcUrl;
    my.sound_downFile = new p5.SoundFile(
      srcUrl, //
      sound_cb_ok,
      sound_cb_error,
      sound_cb_while
    );
  };
  reader.readAsDataURL(blob);

  function sound_cb_ok() {
    // console.log('sound_cb_ok');
    sound_downloaded();
  }

  function sound_cb_error(error) {
    ui_error('sound_cb_error', error);
  }

  function sound_cb_while(prog) {
    // console.log('sound_cb_while prog', prog);
  }
}

function my_init() {
  //
  pixelDensity(1);

  // my.dbStoreRootPath = '-mo-storage-video-@w-';
  my.dbStoreRootPath = '-mo-storage-dashboard-@w-';
  console.log('my.dbStoreRootPath', my.dbStoreRootPath);

  init_clipsName();

  if (my.png) {
    // png image type preserves white background
    my.imageType = 'image/png';
    my.imagExt = '.png';
  } else {
    // jpeg give black background
    my.imageType = 'image/jpeg';
    my.imagExt = '.jpg';
  }
  if (windowWidth < 600) {
    my.width = windowWidth;
    my.height = windowHeight;
  }
  if (my.vFlip) {
    let temp = my.width;
    my.width = my.height;
    my.height = temp;
  }

  // image are store in sequence
  // {my.dbStoreRootPath}/clips/${nums}${my.imagExt}
  my.image_seq_pad = 3;

  init_layer();

  my.next_secs = millis() / 1000 + my.interval;

  init_counts();

  if (my.soundEnable) {
    sound_init();
  }

  my.img_download_log = 0;
}

function init_clipsName() {
  let today = new Date();
  let str = today.toISOString();
  clipsName_restore();
  my.clipsName += '/' + str.substring(0, 10);
}

function clipsName_restore() {
  let nclipsName = localStorage.getItem('clipsName');
  console.log('clipsName_restore nclipsName', nclipsName);
  if (!nclipsName) {
    return;
  }
  my.clipsName = nclipsName;
}

function clipsName_update() {
  console.log('clipsName_update my.clipsName', my.clipsName);
  let parts = my.clipsName.split('/');
  localStorage.setItem('clipsName', parts[0]);
}

function init_layer() {
  let w = int(my.width / my.rezScale);
  let h = int(my.height / my.rezScale);
  my.layer = createGraphics(w, h);
  my.vwidth = w;
  my.vheight = h;
