function load_setup_local(setup_in) {   // video selector
  let in_keys = Object.keys(setup_in);
  for (var i = 0; i < in_keys.length; i++) {
    control[in_keys[i]] = setup_in[in_keys[i]];
    // sliders are 2 - 11]
