      sliders_all[in_keys[i]].value(control[in_keys[i]]);
  }
  change_video(control.vd_sel);
  rst_opt = control.px_rst;
  if (control.px_ftc > 0)
    time_filter_on = true;
  if (control.sk_sze > 0)
    stroke_enable = true;
