    video_selected.loadPixels();  // load all pixels from video



//    if (enable_zoom) {
      temp_video_canvas.image(video_selected, 0,0, 320*2, 180*2); //  (320-320*mulll)/2, (180-180*mulll)/2, 320*mul, 180*mulll);
      temp_video_canvas.loadPixels();
