function draw_timed() {
  let func = drawers[drawers_index];
  func();
  // if (aRun && frameCount % 60 == 0) {
  //   next_action()
  // }
  let now = millis() / 1000.0
  if (aRun && now > aNextSecs) {
    aDelayIndex = (aDelayIndex + 1) % aDelays.length;
    aDelay = aDelays[aDelayIndex]
    aNextSecs = now + aDelay;
    next_action();
  }
}

