let particles = [];
let slider;

function setup_paint() {
  for (var i = 0; i < 200; i++) {
    particles[i] = new Particle(random(width), random(height));
  }
  slider = createSlider(0, 255, 20);
  background(0);
}

function draw_paint() {
  if (! video.loadedmetadata) return;
  video.loadPixels();
  for (let i = 0; i < particles.length; i++) {
    particles[i].run();
  }
}

class Particle {
  constructor(x, y) {
    this.x = x;
    this.y = y;
    this.r = random(4, 32);
    this.w1 = width / 3;
    this.w2 = this.w1 * 2;
    this.w3 = this.w1 * 3;
    this.xstart = 0;
    this.xend = width;
  }

  run() {
    this.update();
    this.show();
  }

  update() {
    this.x += random(-10, 10);
    this.y += random(-10, 10);

    this.x = constrain(this.x, this.xstart, this.xend);
    this.y = constrain(this.y, 0, height);
  }

  show() {
    noStroke();
    let px = floor(this.x / vScale);
    let py = floor(this.y / vScale);
    let col = video.get(px, py);
    //console.log(col);
    fill(col[0], col[1], col[2], slider.value());
    ellipse(this.x, this.y, this.r, this.r);
  }
}

let aLayer;

function setup_random() {
  aLayer = createGraphics(width, height)
  aLayer.clear();
}

function draw_random() {
  // background(51);

  // let w1 = int(width / 3);
  // let w2 = int(w1 * 2);

  aLayer.loadPixels();
  for (let y = 0; y < height; y++) {
    for (let x = 0; x < width; x++) {
      let index = (x + y * width) * 4;
      aLayer.pixels[index + 0] = x;
      aLayer.pixels[index + 1] = random(255);
      aLayer.pixels[index + 2] = y;
      aLayer.pixels[index + 3] = 255;
    }
  }
  aLayer.updatePixels();

  // image(aLayer, 0, 0)
}

let video;
let vScale = 16;

function setup_bright() {
  video = createCapture(VIDEO);
  video.size(width / vScale, height / vScale);
}

function draw_bright() {
  if (! video.loadedmetadata) return;

  // background(51);
  video.loadPixels();

  let w1 = 0;
  let w2 = video.width;

  fill(51);
  rectMode(CORNER);
  rect(w1 * vScale, 0, w2 * vScale, height);

  for (let y = 0; y < video.height; y++) {
    for (let x = w1; x < w2; x++) {
      let index = (video.width - x + 1 + y * video.width) * 4;
      // console.log('rgb', r, g, b)
      let r = video.pixels[index + 0];
      let g = video.pixels[index + 1];
      let b = video.pixels[index + 2];
      let bright = (r + g + b) / 3;
      let w = map(bright, 0, 255, 0, vScale);
      noStroke();
      fill(255);
      rectMode(CENTER);
      w = int(w)
      let nx = int(x * vScale)
      let ny = int(y * vScale)
      if (w > 0) {
        // console.log('nx, ny, w', nx, ny, w)
        // let img = aLayer.get(nx, ny, w, w)
        // image(img, dx, dy, dWidth, dHeight, sx, sy, [sWidth], [sHeight] 
        image(aLayer, nx, ny, w, w, nx, ny, w, w)        
      }
      // rect(nx, ny, w, w);
    }
  }
}

