      let index = (x + y * width) * 4;
      r=pixels[index + 0];
      g=pixels[index + 1];
      b=pixels[index + 2];
      a=pixels[index + 3];

    }
  }
  updatePixels;
    image(videoIn, 0, 0, width, height);
}

